void singal_handle_func(int signo)
{
    if (signo == SIGINT)
    {
        printf("Receive CTRL + C\n");
        exit(1);
    }
    if (signo == SIGUSR1)
    {
        printf("Receive user defined signal");
    }
}

int main()
{
    if (SIG_ERR == signal(SIGINT, singal_handle_func))
    {
        printf("Set signal SIGINT handle");
    }
