struct Node{
    int data;
    int status; //白:0, 灰:1, 黑:2
    vector neigh; 
}; 

void dfs(Node* first){
    first->status = 1;
    first->data; // use data
    for(int i=0 ; ineigh.size() ; i++){
        if(first->neigh[i]->status == 0) 
            dfs(first->neigh[i]);
        else
            // cycle
    }
    return;
}

